import React, { useState, useEffect } from 'react';
import { Col, Row } from 'reactstrap';
import { Bar } from 'react-chartjs-3';
import { Loading } from 'components';

const data = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
    datasets: [
        {
            label: 'All Beds',
            backgroundColor: 'rgba(1,104,250,0.2)',
            borderColor: 'rgba(1,104,250,1)',
            borderWidth: 1,
            hoverBackgroundColor: 'rgba(1,104,250,0.4)',
            hoverBorderColor: 'rgba(1,104,250,1)',
            data: [65, 59, 80, 81, 56, 55, 40]
        },
        {
            label: 'ICU Beds',
            backgroundColor: 'rgba(253,126,20,0.2)',
            borderColor: 'rgba(253,126,20,1)',
            borderWidth: 1,
            hoverBackgroundColor: 'rgba(253,126,20,0.4)',
            hoverBorderColor: 'rgba(253,126,20,1)',
            data: [45, 23, 77, 81, 66, 57, 40]
        },
        {
            label: 'Bed Shortages',
            backgroundColor: 'rgba(255,99,132,0.2)',
            borderColor: 'rgba(255,99,132,1)',
            borderWidth: 1,
            hoverBackgroundColor: 'rgba(255,99,132,0.4)',
            hoverBorderColor: 'rgba(255,99,132,1)',
            data: [12, 2, 55, 33, 23, 25, 77]
        }
    ]
};

const NoData = () => {
    return (
        <Row>
            <Col xs={12} className="text-center">
                <Row>
                    <Col sm={2} />
                    <Col xs={12} sm={8} className="text-center mt-4">
                        &nbsp;
                    </Col>
                </Row>
                <Row>
                    <Col xs={12} className="text-center text-muted mt-5 mb-5">
                        <strong>Stats here...</strong>
                    </Col>
                </Row>
            </Col>
        </Row>
    );
}

function BarChart() {
    const [loaded, setLoaded] = useState(false);
    const [chartData, setChartData] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            setChartData(data);
            setLoaded(true);
        };
        fetchData();
    }, []);

    return (
        <React.Fragment>
            {!loaded && <Loading />}
            {loaded && chartData && <Bar data={chartData} />}
            {loaded && !chartData && <NoData />}
        </React.Fragment>
    );
}

export default BarChart;
