// Send SMS using Sinch

// This sample can be 'browserified' to run in the browser (Please see browserify.org)
//    browserify send_sms.js | uglifyjs > bundle.js

var sinchRestApi = require('../index.js')({
		key: 'YOUR_APPLICATION_KEY', 
		secret: 'YOUR_APPLICATION_SECRET'
	}); 

var consoleOut = function(result) { console.log(result); }

sinchRestApi.messaging.sendSms({number: '+46738000099', message: 'Hello World!'})
	.then(consoleOut)
	.fail(consoleOut)