var sinchApi = require('sinch-rest-api')(require('./credentials.json'));

var number;
if(process.argv.length > 2) {
    number = process.argv[2];
}
else {
    console.error("Pass phone number with leading plus (ie. +1555123123) as first parameter");
    return;
}

var consoleOut = function(result) { console.log(result); }

sinchApi.verification.verificationRequest({identity: {"type": "number", "endpoint": number}, method: "flashcall"})
	.then(consoleOut)
	.fail(consoleOut)